import React from "react";
import { Row, Col, Button } from "reactstrap";

import Widget from "../../components/Widget";
import s from './Analysis.module.scss';
import CircleProgress from '../../components/CircleProgress/'

// анимация отрисовки круга
let squareProgress = (function(selector, categories) {
	let wrapper = document.querySelectorAll(selector);
	Array.prototype.forEach.call(wrapper, function(wrapper, i) {
		let percent,
			newPercent,
			from,
			to,
			duration,
			start,
			fullSquares,
			opacitySquare,
			progressBlocks;

		let getValues = function() {
			percent = categories[i][1] * 10;
			newPercent = 0;
			from = 0;
			to = percent;
			duration = 3000;
			start = new Date().getTime();
		};
		function animate() {
			requestAnimationFrame(animate);
			let time = new Date().getTime() - start;
			if (time <= duration) {
				let x = easeInOutQuart(time, from, to - from, duration);
				newPercent = x;
				drawSquares(newPercent);
			}
		}

		function drawSquares(newPercent) {
			progressBlocks = wrapper.getElementsByClassName(`${s.progressBlock}`);
			fullSquares = Math.trunc(newPercent / 10);
			opacitySquare = newPercent % 10 / 10;
			for (let i = 0; i < fullSquares; i++) {
				progressBlocks[i].style.opacity = 1;
				progressBlocks[i].setAttribute('class', `${s.progressBlock} ${s.active}`)
				progressBlocks[i].style.backgroundColor = `${getColor(newPercent / 100)}`
			}
			progressBlocks[fullSquares].setAttribute('class', `${s.progressBlock} ${s.active}`)
			progressBlocks[fullSquares].style.opacity = opacitySquare;
			progressBlocks[fullSquares].style.backgroundColor = `${getColor(newPercent / 100)}`
		}

		let update = function() {
			let progressBars = document.getElementsByClassName(`${s.progressBar}`)
			for (let i = 0; i < progressBars.length; i++) {
				for (let j = 0; j < progressBars[i].childNodes.length; j++) {
					let el = progressBars[i].childNodes[j]
					el.setAttribute('class', `${s.progressBlock}`)
					el.style.backgroundColor = `transparent`;
				}
			}
			getValues();
			animate();
		}
		update();

		let resizeTimer;
		window.addEventListener("resize", function() {
			clearTimeout(resizeTimer);
			resizeTimer = setTimeout(function() {
				clearTimeout(resizeTimer);
				start = new Date().getTime();
				update();
			}, 250);
		});
	});
	
	function easeInOutQuart(t, b, c, d) {
		if ((t /= d / 2) < 1) return c / 2 * t * t * t * t + b;
		return -c / 2 * ((t -= 2) * t * t * t - 2) + b;
	}
});

function setSquares(categories) {
	let labels = document.getElementsByClassName(`${s.label}`);
	for (let l = 0; l < labels.length; l++) {
		labels[l].innerHTML = `${categories[l][0]} <span class=${s.descriptionLabel}>${categories[l][1]}/10</span>`;
	}
	squareProgress(`.${s.progressBar}`, categories);
}

function setCircle(rating) {
	let el = document.getElementsByClassName(`${s.counter}`)[0];
	el.setAttribute('data-cp-percentage', rating);
	innerHTML = `<span class=${s.percentage}><strong>` + 
				percent + '</strong> %</span><canvas class="circleProgressCanvas" width="' + 
				(wrapperWidth * 2) + '" height="' + wrapperHeight * 2 + '"></canvas>';
	wrapper.innerHTML = innerHTML;
	text = document.getElementsByClassName(`${s.percentage}`)[i]
	CircleProgress(`.${s.counter}`, text);
}

function setInfo() {
	let rating = document.getElementsByClassName('reviewArea')[0].value;
	if (!rating) { return; }

	// появление блока с результами анализа
	let widgets = document.getElementsByClassName(`${s.columnReviews}`);
	for (let i = 0; i < widgets.length; i++) {
		widgets[i].setAttribute('style', '');
	}

	let categoriesList = [['Дебетовые карты', rating], ['Ипотека', 5], ['Кредит', 3]]
	setSquares(categoriesList);
	setCircle(rating);
}

function getColor(value) {
	var hue = (value * 120).toString(10);
	return ["hsl(", hue, ",100%,50%)"].join("");
}

const Reviews = () => (
	<div>
		<h1 className="page-title">
			Анализ отзыва
		</h1>
		<Row>
			<Col xs={12} lg={6}>
