import React from "react";
import { Row, Col, Button } from "reactstrap";

import Widget from "../../components/Widget";
import s from './Reviews.module.scss';

function reset_animation() {
	var el = document.querySelector('.progressTest')
	Reviews.get
	console.log(el)
	// var el = document.getElementsByClass('progress')[0];
	el.style.animation = 'none';
	// el.offsetHeight; /* trigger reflow */
	el.style.animation = null; 
	return 0;
}

const Reviews = () => (
	<div>
		<h1 className="page-title">
			Reviews - <span className="fw-semi-bold">Getting an analysis</span>
		</h1>
		<Widget
			title={
				<h5>
					Headings{" "}
					<small className="text-muted">Default and customized</small>
				</h5>
			}
		>
			<h4>Default headings</h4>
			<p>Basic headings for everyday use</p>
			<form>
				<div class="mb-3" className="w-100 h-100 border rounded">
					<textarea type="text" rows="5" style={{ background: "transparent" }} class="form-control" placeholder="Введите текст вашего отзыва здесь..."></textarea>
				</div>
				<Button color="default" style={{ "margin-top": "1.5rem" }} className="mr-2" size="sm">Отправить</Button>
			</form>
		</Widget>
		<button onclick={reset_animation()}>Reset</button>
		<div class="row d-flex justify-content-center" className={s.mt100}>
			<div class="col-md-6">
			    <div className={`${s.progress} ${s.blue} progressTest`}>
					<span className={s.progressLeft}>
								   <span className={s.progressBar}></span>
					</span>
					<span className={s.progressRight}>
								   <span className={s.progressBar}></span>
					</span>
					<div className={s.progressValue}>90%</div>
				</div> 
				<div className={`${s.progress} ${s.yellow}`}>
				    <span className={s.progressLeft}>
								   <span className={s.progressBar}></span>
				    </span>
				    <span className={s.progressRight}>
								   <span className={s.progressBar}></span>
				    </span>
				    <div className={s.progressValue}>37.5%</div>
				</div>
		    </div>
	    </div>
	</div>
);

export default Reviews;
