import React from "react";
import { Row, Col, Button } from "reactstrap";

import Widget from "../../components/Widget";
import s from './Reviews.module.scss';


function getColor(value) {
  //value from 0 to 1
  var hue = (value * 120).toString(10);
  console.log(hue)
  return ["hsl(", hue, ",100%,50%)"].join("");
}

var slider = document.getElementById("myRange");
var output = document.getElementById("demo");
console.log(output)
output.innerHTML = slider.value;

slider.oninput = function() {
  output.innerHTML = this.value;
  this.style.backgroundColor = getColor(this.value / 100);
}
//initial trigger so turns the right colour.
slider.oninput();

// анимация отрисовки круга
let circleProgress = (function(selector) {
	let wrapper = document.querySelectorAll(selector);
	Array.prototype.forEach.call(wrapper, function(wrapper, i) {
		let wrapperWidth,
			wrapperHeight,
			percent,
			innerHTML,
			canvas,
			context,
			lineWidth,
			centerX,
			centerY,
			radius,
			newPercent,
			speed,
			from,
			to,
			duration,
			start,
			strokeStyle,
			text;

		let getValues = function() {
			wrapperWidth = parseInt(window.getComputedStyle(wrapper).width);
			wrapperHeight = wrapperWidth;
			percent = wrapper.getAttribute('data-cp-percentage');
			innerHTML = `<span class=${s.percentage}><strong>` + percent + '</strong> %</span><canvas class="circleProgressCanvas" width="' + (wrapperWidth * 2) + '" height="' + wrapperHeight * 2 + '"></canvas>';
			wrapper.innerHTML = innerHTML;
			text = document.getElementsByClassName(`${s.percentage}`)[i]
			canvas = document.getElementsByClassName('circleProgressCanvas')[i]
			wrapper.style.height = canvas.style.width = canvas.style.height = wrapperWidth + "px";
			context = canvas.getContext('2d');
			centerX = canvas.width / 2;
			centerY = canvas.height / 2;
			newPercent = 0;
			speed = 1;
			from = 0;
			to = percent;
			duration = 3000;
			lineWidth = 40;
			radius = canvas.width / 2 - lineWidth;
			strokeStyle = wrapper.getAttribute('data-cp-color');
			start = new Date().getTime();
		};

		function animate() {
			requestAnimationFrame(animate);
			let time = new Date().getTime() - start;
			if (time <= duration) {
				let x = easeInOutQuart(time, from, to - from, duration);
				newPercent = x;
				text.innerHTML = newPercent.toFixed(1);
				text.style.color = 'white';
				drawArc();
			}
		}

		function drawArc() {
			let circleStart = 1.5 * Math.PI;
			let circleEnd = circleStart + (newPercent / 2.5) * Math.PI;
			context.clearRect(0, 0, canvas.width, canvas.height);
			context.beginPath();
			context.arc(centerX, centerY, radius, circleStart, 4 * Math.PI, false);
			context.lineWidth = lineWidth;
			context.strokeStyle = 'transparent'; // #acd1e3
			context.stroke();
			context.beginPath();
			context.arc(centerX, centerY, radius, circleStart, circleEnd, false);
			context.lineWidth = lineWidth;
			context.strokeStyle = strokeStyle;
			context.stroke();
		}

		let update = function() {
			getValues();
			animate();
		}
		update();

		let resizeTimer;
		window.addEventListener("resize", function() {
			clearTimeout(resizeTimer);
			resizeTimer = setTimeout(function() {
				clearTimeout(resizeTimer);
				start = new Date().getTime();
				update();
			}, 250);
		});
	});

	// http://easings.net/#easeInOutQuart
	//  t: current time
	//  b: beginning value
	//  c: change in value
	//  d: duration
	//
	function easeInOutQuart(t, b, c, d) {
		if ((t /= d / 2) < 1) return c / 2 * t * t * t * t + b;
		return -c / 2 * ((t -= 2) * t * t * t - 2) + b;
	}
});

function showCircle() {
	let el = document.getElementsByClassName(`${s.counter}`)[0];
	let rating = document.getElementsByClassName('reviewArea')[0].value;
	if (!rating) { return; }
	el.setAttribute('data-cp-percentage', rating)
	circleProgress(`.${s.counter}`);
}
						
const Reviews = () => (
	<div>
		<h1 className="page-title">
			Reviews - <span className="fw-semi-bold">Getting an analysis</span>
		</h1>
		<Row>
			<Col xs={12} lg={6}>
				<Widget
					title={
						<h5>
							Headings{" "}
							<small className="text-muted">Default and customized</small>
						</h5>
					}
				>
					<h4>Default headings</h4>
					<p>Basic headings for everyday use</p>
					<form>
						<div className="mb-3 w-100 h-100 border rounded">
							<textarea type="text" rows="5" style={{ background: "transparent" }} className="form-control reviewArea" placeholder="Введите текст вашего отзыва здесь..."></textarea>
						</div>
						<Button onClick={showCircle} color="default" style={{ "margin-top": "1.5rem" }} className="mr-2" size="sm">Отправить</Button>
					</form>
				</Widget>
			</Col>
			<Col xs={12} lg={6}>
				<Widget
					title={
						<h5>
							Body texts <small className="text-muted">Variations</small>
						</h5>
					}
				>
					<h4>Basic texts</h4>
					<p>Styling for common texts</p>
					<div className={s.bgStyle}>
						<div>
							<div className={s.counter} data-cp-percentage="0" data-cp-color="#00bfeb"> </div>
						</div>
					</div>
					<div class="slidecontainer">
						<input type="range" min="1" max="100" value="50" class="slider" id="myRange"></input>
						<p>Value: <span id="demo"></span></p>
					</div>
				</Widget>
			</Col>
		</Row>
	</div>
);

export default Reviews;
